sys_variable_value.value:
/*
  Workspace Polling
    Finds any records not in an applied state then
    queries the Terraform Enterprise API for their status.
*/

// System Property variables for Terraform API
var conf = new tfe_config();

// Search Terraform Table for any records not in "applied" state
var gr = new GlideRecord("x_325709_terraform_terraform");
gr.addQuery("last_known_state", "!=", "applied");
gr.query();

// Loop through all records
while (gr.next()) {
	var runID;

  // Fetch Workspace Current Run Info
  var currentRun = getWorkspaceCurrentRun(gr.workspace_name);
  if (currentRun !== null) {
    runID = currentRun.id;
  } else {
    gr.last_known_state = String(currentRun);
    gr.update();
    continue; // move on to next record
  }

  // Fetch Run State Info and update record
  var runState = getRunState(runID);
  gr.last_known_state = runState.toLowerCase();
  gr.update();

} // end while loop


// ** Functions **
/*
* @param {String} run_id - Run ID
* @return {String} Current Run State String
*/
function getRunState(run_id) {
	gs.info("Run id" + run_id);
	var current_run_state;

	var runResponse;
	var runResponseBody;
	var runHttpStatus;

	getRun = new sn_ws.RESTMessageV2("TFE Runs", "Get Run");
	getRun.setHttpTimeout(10000);
	getRun.setStringParameter("hostname", conf.hostname);
	getRun.setStringParameter("api_team_token", conf.api_team_token);
	getRun.setStringParameter("run_id", run_id);

	try {
		runResponse     = getRun.execute();
		runResponseBody = runResponse.haveError() ? runResponse.getErrorMessage() : runResponse.getBody();
	} catch(ex) {
		// On HTTP Request error
		var errorMsg    = ex.getMessage();
		runHttpStatus   = runResponse.getStatusCode();
		var requestBody = getRun ? getRun.getRequestBody() : null;

	  gs.info("Response: " + errorMsg);
	  gs.info("HTTP Status: " + runHttpStatus);
	} finally {
	  var parsedRun     = JSON.parse(runResponseBody);
    current_run_state = String(parsedRun["data"]["attributes"]["status"]);
	}

  return current_run_state;
}


/*
* @param {String} workspace_name - Name of the workspace to query TFE API
* @return {Object|null} Parsed object from JSON data or null
*/
function getWorkspaceCurrentRun(workspace_name) {
	var currentRunData;

	var workspaceResponse;
	var workspaceResponseBody;
	var responseStatus;

  getWorkspace = new sn_ws.RESTMessageV2("TFE Workspaces", "Show Workspace");
	getWorkspace.setHttpTimeout(10000);
	getWorkspace.setStringParameter("hostname", conf.hostname);
	getWorkspace.setStringParameter("api_team_token", conf.api_team_token);
	getWorkspace.setStringParameter("org_name", conf.org_name);
	getWorkspace.setStringParameter("workspace_name", workspace_name);

	try {
		workspaceResponse     = getWorkspace.execute();
		workspaceResponseBody = workspaceResponse.haveError() ? workspaceResponse.getErrorMessage() : workspaceResponse.getBody();

	} catch(ex) {
		// On HTTP Request error
		var errorResponseBody = ex.getMessage();
		responseStatus        = workspaceResponse.getStatusCode();

	  gs.info("Response: " + errorResponseBody);
	  gs.info("HTTP Status: " + responseStatus);
	} finally {
		var parsedWorkspace = JSON.parse(workspaceResponseBody);
    currentRunData = parsedWorkspace["data"]["relationships"]["current-run"]["data"];
  }

  return currentRunData;
}

