sys_variable_value.value:
gs.info("Workflow: Terraform Provision Workspace with Variables Started...");

// ** Set to the Variable Set for this Catalog Item **
// ** Any variables in the set must start with `tf_`
VAR_SET_NAME = "var_create_workspace";
tf_variables = current.variables[VAR_SET_NAME];

// Create Workspace
var conf          = new tfe_config();
var requestID     = current.request.getDisplayValue();        // Catalog Request ID
var workspaceVCS  = current.variables.tf_var_vcs_repository;  // Variable from Catalog Request
var workspaceName = createWorkspaceName(requestID);
var workspaceURL  = createWorkspaceURL(conf, workspaceName);

var vcsRecord               = getVCSRecord(workspaceVCS);
var createWorkspaceContent  = createWorkspaceRequestContent(vcsRecord, workspaceName);
var workspaceRequestContent = JSON.stringify(createWorkspaceContent);
var workspaceID             = createWorkspace(conf, workspaceRequestContent);

// Create any TF Variables from the Variable Set.
// ** tf_var_ will be removed from variable name **
Object.keys(tf_variables).forEach(function(key) {
  var _key = String(key);

  if (_key.startsWith("tf_var_")) {
    var _key_without_tf   = _key.replace("tf_var_","");
    var createVarContent  = createVariableRequestContent(workspaceID, _key_without_tf, String(tf_variables[key]));
    var varRequestContent = JSON.stringify(createVarContent);
    createVariables(conf, varRequestContent);
  }
});

// Create a run for workspace
// Workspace was created with "auto-apply"
createRun(conf, workspaceID, requestID);

// ** Functions **
/*
* @param {Object} conf - Object of configuration variables for Terraform
* @param {Object} reqContent - Stringified JSON for request content
* @return {String} Workspace ID
*/
function createWorkspace(conf, reqContent) {
  var workspace_id = "";

  var requestBody;
  var responseBody;
  var status;
  // Might throw exception if message doesn't exist or not visible due to scope.
  var sm = new sn_ws.RESTMessageV2("TFE Workspaces", "Create Workspace");

  // Set Request variables
  // sm.setEndpoint(conf.hostname);
  sm.setHttpTimeout(10000); // Wait at most 10 seconds for response from http request.
  sm.setStringParameter("hostname", conf.hostname);
  sm.setStringParameter("org_name", conf.org_name);
  sm.setStringParameter("api_team_token", conf.api_team_token);
  sm.setRequestBody(reqContent);

  // ** Execute request **
  try{
    // Might throw exception if http connection timed out or auth error
    var response = sm.execute();

    responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
    status       = response.getStatusCode();
  } catch(ex) {
    // On HTTP Request error
    responseBody = ex.getMessage();
    requestBody  = sm ? sm.getRequestBody() : null;
    status       = response ? response.getStatusCode() : "500";

    // Log to System Logs
    gs.error("Error making HTTP Request");
    gs.error("HTTP Status: " + status);
    gs.error("Request Body: " + requestBody);
    gs.error("Response: " + responseBody);
  } finally {
    //  parse JSON string returned from request into a JSON object
    var parsed = JSON.parse(responseBody);
    createTerraformRecord(parsed, requestID, workspaceURL);

    workspace_id = parsed.data.id;
  }

  return workspace_id;
}

/*
* @param {Object} conf - Object of configuration variables for Terraform
* @param {Object} reqContent - Stringified JSON for request content
*/
function createVariables(conf, reqContent) {
  var requestBody;
  var responseBody;
  var status;
  // Might throw exception if message doesn't exist or not visible due to scope.
  var sm = new sn_ws.RESTMessageV2("TF Variables", "Create Variable");

  // Set Request variables
  // sm.setEndpoint(conf.hostname);
  sm.setHttpTimeout(10000); // Wait at most 10 seconds for response from http request.
  sm.setStringParameter("hostname", conf.hostname);
  sm.setStringParameter("org_name", conf.org_name);
  sm.setStringParameter("api_team_token", conf.api_team_token);
  sm.setRequestBody(reqContent);

  // ** Execute request **
  try{
    // Might throw exception if http connection timed out or auth error
    var response = sm.execute();

    responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
    status       = response.getStatusCode();
  } catch(ex) {
    // On HTTP Request error
    responseBody = ex.getMessage();
    requestBody  = sm ? sm.getRequestBody() : null;
    status       = response ? response.getStatusCode() : "500";

    // Log to System Logs
    gs.error("Error making HTTP Request");
    gs.error("HTTP Status: " + status);
    gs.error("Request Body: " + requestBody);
    gs.error("Response: " + responseBody);
  } finally {

  }
}

/*
* @param {Object} conf         - Object of configuration variables for Terraform
* @param {String} workspace_id - Workspace ID on Terraform
* @param {String} request_id   - Request ID on ServiceNow
*/
function createRun(conf, workspace_id, request_id) {
  // Give TF Server 5 seconds to create the workspace and variables.
  sleep(5000);

  var requestBody;
  var responseBody;
  var status;

  var sm = new sn_ws.RESTMessageV2("TFE Runs", "Create Run");
  sm.setHttpTimeout(10000); // Wait at most 10 seconds for response from http request.
  sm.setStringParameter("hostname", conf.hostname);
  sm.setStringParameter("api_team_token", conf.api_team_token);
  sm.setStringParameter("workspace_id", String(workspace_id));
  sm.setStringParameter("message_content", "RequestID: " + request_id);

  // ** Execute request **
  try{
    var response = sm.execute();

    responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
    status       = response.getStatusCode();
  } catch(ex) {
    // On HTTP Request error
    requestBody  = sm ? sm.getRequestBody() : null;
    status       = response ? response.getStatusCode() : "500";
    responseBody = ex.getMessage();

    // Log to System Logs
    gs.error("Error making HTTP Request");
    gs.error("Request Body: " + requestBody);
    gs.error("Response: " + responseBody);
  } finally {

  }
}

/*
* @param {Object} obj - Object of parsed response
* @param {String} request_id - Catalog Request Number/ID
*/
function createTerraformRecord(obj, request_id, workspace_url) {
  var grTerraform = new GlideRecord("x_325709_terraform_terraform");
  grTerraform.initialize();
  grTerraform.request_id        = request_id;
  grTerraform.tfe_action_type   = "Create Workspace";
  grTerraform.workspace_id      = obj.data.id;
  grTerraform.tfe_workspace_url = workspace_url;
  grTerraform.workspace_name    = obj.data.attributes.name;
  grTerraform.last_known_state  = "null"; // default until run status is updated
  grTerraform.insert();
}

/*
* @return {String} Workspace Name
*/
function createWorkspaceURL(conf, workspaceName) {
  var formatedURL = conf.hostname + "/app/" + conf.org_name + "/workspaces/" + workspaceName;

  return formatedURL;
}

/*
* @param {String} name
* @return {String} Formatted name for the created workspace
*/
function createWorkspaceName(name) {
  // Abstraction for a unique workspace name
  return name;
}

/*
* @param {String} workspaceVCS - Workspace VCS Name
* @return {Object} GlideRecord for VCS
*/
function getVCSRecord(vcs_name) {
  var record;
  var grVCS = new GlideRecord("x_325709_terraform_vcs_repositories");
  grVCS.addQuery("name", vcs_name);
  grVCS.setLimit(1);
  grVCS.query();
  while (grVCS.next()) {
    record = grVCS;
  }

  return record;
}

/*
* @param {Object} vcsObject - VCS Glide Record
* @param {String} workspaceName - Name of the workspace being created
* @return {Object} Data object for create workspace request content
*/
function createWorkspaceRequestContent(vcsObject, workspaceName) {
  var data = {
    "data": {
      "attributes": {
        "name": String(workspaceName),
        "queue-all-runs": "false",
        "auto-apply": "true",
        "terraform_version": String(vcsObject.terraform_version),
        "ingress-submodules": "false",
        "working-directory": "",
        "vcs-repo": {
          "identifier": String(vcsObject.identifier),
          "oauth-token-id": String(vcsObject.oauth_token_id),
          "branch": "",
          "default-branch": "true"
        }
      },
      "type": "workspaces"
    }
  };

  return data;
}

/*
* @param {String} workspace_id - Workspace ID
* @param {String} key          - TF Var key/name
* @param {String} value        - TF VAR value
* @return {Object} Data object for create variable request content
*/
function createVariableRequestContent(workspace_id, key, value) {
  var data = {
    "data": {
      "type": "vars",
      "attributes": {
        "key": key,
        "value": value,
        "category": "terraform",
        "hcl": false,
        "sensitive": false
      },
      "relationships": {
        "workspace": {
          "data": {
            "id": String(workspace_id),
            "type": "workspaces"
          }
        }
      }
    }
  };

  return data;
}

/*
  Shhhhh go to sleepzzzz and hack around SN blocking sleep
  @param {Integer} ms - milliseconds to sleep.
*/
function sleep(ms) {
  var unixtime_ms = new Date().getTime();
  while(new Date().getTime() < unixtime_ms + ms) {}
}

